var apicontext = {
    post: (data, url, callback) => {
        $.ajax({
            method: "POST",
            url: url,
            data: data
        })
            .done(function (response) {
                callback(response)
            });
    },
    postFormData: (data, url, callback) => {
        $.ajax({
            method: "POST",
            url: url,
            data: data,
            processData: false,
            contentType: false
        })
            .done(function (response) {
                callback(response)
            });
    }
};

var notify = {
    success: (mess, title) => {
        toastr.success(mess, title, {
            timeOut: "1000"
        });
    },
    failure: (mess, title) => {
        toastr.error(mess, title, {
            timeOut: "1000"
        });
    }
}

$.input = {
    setvalue: (elm, value) => {
        let kind = elm.attributes["kind"].value;
        if (kind === 'text') {
            $(elm).val(value);
        }
        if (kind === 'radio') {
            if (elm.value === value) {
                elm.checked = true;
            }
        }
        if (kind === 'date') {
            try {                
                $(elm).datepicker('setDate', new Date(value.replace(/(\d{2})\/(\d{2})\/(\d{4})/, "$2/$1/$3")));
            } catch {}
            
        }
    }
}