var apicontext = {
    post: (data, url, callback) => {
        $.ajax({
            method: "POST",
            url: url,
            data: data,
            processData: false,
            contentType: false
        })
            .done(function (response) {
                callback(response)
            });
    }
};

var notify = {
    success: (mess, title) => {
        toastr.success(mess, title, {
            timeOut: "1000"
        });
    },
    failure: (mess, title) => {
        toastr.error(mess, title, {
            timeOut: "1000"
        });
    }
}